import './Expenses.css';
import { useState } from 'react';
import ExpensesFilter from './ExpenseFilter';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';

function Expenses(props) {
    const [theYear, setTheYear] = useState('2023');
    const filteredExpenses = props.items.filter(expense => {
        return expense.date.getFullYear().toString() === theYear;
    });

    let expensesContent = <p>No expenses listed for this year.</p>

    if (expensesContent.length > 0) {
        expensesContent = filteredExpenses.map((expense) =>
        <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date} />)
    }

    const filterHandler = selYear => {
        setTheYear(selYear);
    };

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selected={theYear} onChangeFilter={filterHandler} />
                {expensesContent}
            </Card>
        </div>
    );
}

export default Expenses;
